// MODULES //

#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/special/sqrt.h"
#include "stdlib/math/base/special/riemann_zeta.h"
#include "stdlib/constants/float64/pi.h"


// MAIN //

/**
* Returns the skewness for a Gumbel distribution with location `mu` and scale `beta`.
*
* @param {number} mu - location parameter
* @param {PositiveNumber} beta - scale parameter
* @returns {number} skewness
*
* @example
* double y = stdlib_base_dists_gumbel_skewness( 0.0, 1.0 );
* // returns ~1.14
*
* @example
* double y = stdlib_base_dists_gumbel_skewness( 5.0, 2.0 );
* // returns ~1.14
*
* @example
* double y = stdlib_base_dists_gumbel_skewness( NaN, 1.0 );
* // returns NaN
*
* @example
* double y = stdlib_base_dists_gumbel_skewness( 0.0, NaN );
* // returns NaN
*
* @example
* double y = stdlib_base_dists_gumbel_skewness( 0.0, 0.0 );
* // returns NaN
*/
double stdlib_base_dists_gumbel_skewness( const double mu, const double beta ) {
	double SKEWNESS = 12.0 * stdlib_base_sqrt( 6.0 ) * stdlib_base_zeta( 3.0 ) / ( STDLIB_CONSTANT_FLOAT64_PI*STDLIB_CONSTANT_FLOAT64_PI*STDLIB_CONSTANT_FLOAT64_PI );

	if (
		stdlib_base_is_nan( mu ) ||
		stdlib_base_is_nan( beta ) ||
		beta <= 0.0
	) {
		return 0.0/0.0; // NaN
	}
	return SKEWNESS;
}
