// MODULES //

#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/special/pow.h"


// MAIN //

/**
* Evaluates the probability density function (PDF) for a Pareto (Type I) distribution with shape parameter `alpha` and scale parameter `beta` at a value `x`.
*
* @param {number} x - input value
* @param {PositiveNumber} alpha - shape parameter
* @param {PositiveNumber} beta - scale parameter
* @returns {number} evaluated PDF
*
* @example
* double y = pdf( 4.0, 1.0, 1.0 );
* // returns ~0.063
*
* @example
* double y = pdf( 20.0, 1.0, 10.0 );
* // returns 0.025
*
* @example
* double y = pdf( 7.0, 2.0, 6.0 );
* // returns ~0.21
*
* @example
* double y = pdf( 7.0, 6.0, 3.0 );
* // returns ~0.005
*
* @example
* double y = pdf( 1.0, 4.0, 2.0 );
* // returns 0.0
*
* @example
* double y = pdf( 1.5, 4.0, 2.0 );
* // returns 0.0
*
* @example
* double y = pdf( 0.5, -1.0, 0.5 );
* // returns NaN
*
* @example
* double y = pdf( 0.5, 0.5, -1.0 );
* // returns NaN
*
* @example
* double y = pdf( NaN, 1.0, 1.0 );
* // returns NaN
*
* @example
* double y = pdf( 0.5, NaN, 1.0 );
* // returns NaN
*
* @example
* double y = pdf( 0.5, 1.0, NaN );
* // returns NaN
*/
double stdlib_base_dists_pareto_type1_pdf(double x, double alpha, double beta ) {
	double denom;
	double num;
	if (
		stdlib_base_is_nan( x ) ||
		stdlib_base_is_nan( alpha ) ||
		stdlib_base_is_nan( beta ) ||
		alpha <= 0.0 ||
		beta <= 0.0
	) {
		return 0.0; // NaN
	}
	if ( x >= beta ) {
		num = alpha * stdlib_base_pow( beta, alpha );
		denom = stdlib_base_pow( x, alpha + 1.0 );
		return num / denom;
	}
	return 0.0;
}
