// MODULES //

#include "stdlib/math/base/assert/is_nonnegative_integer.h"
#include "stdlib/math/base/special/factorialln.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/special/exp.h"
#include "stdlib/math/base/special/ln.h"
#include "stdlib/constants/float64/pinf.h"


// MAIN //

/**
* Evaluates the probability mass function (PMF) for a Poisson distribution with mean parameter `lambda` at a value `x`.
*
* @param {number} x - input value
* @param {NonNegativeNumber} lambda - mean parameter
* @returns {Probability} evaluated PMF
*
* @example
* double y = stdlib_base_dists_poisson_pmf( 4.0, 3.0 );
* // returns ~0.168
*
* @example
* double y = stdlib_base_dists_poisson_pmf( 1.0, 3.0 );
* // returns ~0.149
*
* @example
* double y = stdlib_base_dists_poisson_pmf( -1.0, 2.0 );
* // returns 0.0
*
* @example
* double y = stdlib_base_dists_poisson_pmf( 0.0, NaN );
* // returns NaN
*
* @example
* double y = stdlib_base_dists_poisson_pmf( NaN, 0.5 );
* // returns NaN
*
* @example
* // Invalid mean parameter:
* double y = stdlib_base_dists_poisson_pmf( 2.0, -0.5 );
* // returns NaN
*/
double stdlib_base_dists_poisson_pmf( const double x, const double lambda ) {
	double lnl;
	if ( stdlib_base_is_nan( x ) || isnan( lambda ) || lambda < 0.0 ) {
		return 0.0/0.0; //NaN
	}
	if ( lambda == 0.0 ) {
		return ( x == 0.0 ) ? 1.0 : 0.0;
	}
	if ( stdlib_base_is_nonnegative_integer( x ) && x != STDLIB_CONSTANT_FLOAT64_PINF ) {
		lnl = (x * stdlib_base_ln( lambda )) - lambda - stdlib_base_factorialln( x );
		return stdlib_base_exp( lnl );
	}
	return 0.0;
}
